                        src={selectedAccountData?.imageUrl}
                        alt={`@${selectedAccountData?.name}`}
                    />
                    <AvatarFallback>
                        {selectedAccountData?.initials}
                    </AvatarFallback>
                </Avatar>

                <span className="sr-only">Open user menu</span>
            </DropdownMenuTrigger>
            <DropdownMenuContent
                side={side}
                align={align}
                className="bg-accent w-[270px] p-0"
            >
                <div className="bg-popover rounded-b-lg p-1 drop-shadow-md">
                    <div className="flex items-center gap-4 p-2">
                        <Avatar className="outline-ring/30 outline-2 outline-offset-2">
                            <AvatarImage
                                src={selectedAccountData?.imageUrl}
                                alt={`@${selectedAccountData?.name}`}
                            />
                            <AvatarFallback>
                                {selectedAccountData?.initials}
                            </AvatarFallback>
                        </Avatar>

                        <div className="flex flex-col gap-1">
                            <p className="text-sm leading-none font-semibold">
                                {selectedAccountData?.name}
                            </p>
                            <p className="text-muted-foreground text-xs leading-none">
                                {selectedAccountData?.email}
                            </p>
                        </div>
                    </div>
                    <DropdownMenuSeparator />
                    {items.map((item, index) => (
                        <DropdownMenuGroup key={item.id}>
                            {item.subItems.map((subItem) => (
                                <DropdownMenuItem key={subItem.label} asChild>
                                    <a
                                        href={subItem.url}
                                        target={
                                            subItem.external
                                                ? '_blank'
                                                : undefined
                                        }
                                    >
                                        {subItem.icon}
                                        <span>{subItem.label}</span>
                                        {subItem.badge ??
                                            (subItem.external ? (
                                                <CircleArrowOutUpRightIcon className="ml-auto" />
                                            ) : (
                                                <DropdownMenuShortcut>
                                                    {subItem.shortcut}
                                                </DropdownMenuShortcut>
                                            ))}
                                    </a>
                                </DropdownMenuItem>
                            ))}

                            {index !== items.length - 1 && (
                                <DropdownMenuSeparator />
                            )}
                        </DropdownMenuGroup>
                    ))}
                </div>

                <DropdownMenuGroup className="bg-accent p-1">
                    <Select
                        value={selectedAccount}
                        onValueChange={handleSelect}
                    >
                        <DropdownMenuItem
                            className="focus:bg-popover/70"
                            asChild
                            onSelect={(e) => e.preventDefault()}
                        >
                            <SelectTrigger className="w-full">
                                <CircleFadingArrowUpIcon />
                                <span>Switch Account</span>

                                <ChevronsUpDownIcon className="ml-auto" />
                            </SelectTrigger>
                        </DropdownMenuItem>

                        <SelectContent>
                            {dummyAccounts.map((acc) => (
                                <SelectItem
                                    key={acc.id}
                                    value={acc.id}
                                    className="flex items-center gap-2 px-3 py-2"
                                >
                                    <Avatar className="outline-ring/30 h-6 w-6 outline-2 outline-offset-2">
                                        <AvatarImage
                                            src={acc.imageUrl}
                                            alt={`@${acc.name}`}
                                        />
                                        <AvatarFallback>
                                            {acc.initials}
                                        </AvatarFallback>
                                    </Avatar>

                                    <div className="flex flex-col gap-1">
                                        <p className="text-sm leading-none font-semibold">
                                            {acc.name}
                                        </p>
                                        <p className="text-muted-foreground text-xs leading-none">
                                            {acc.email}
                                        </p>
                                    </div>
                                </SelectItem>
                            ))}
                        </SelectContent>
                    </Select>

                    <DropdownMenuItem className="focus:bg-popover/70">
                        <LogOutIcon />
                        Sign out
                    </DropdownMenuItem>
                </DropdownMenuGroup>
            </DropdownMenuContent>
        </DropdownMenu>
    )
}

